import React from 'react';
import { Card } from 'react-bootstrap';
import './About.css';
import { Socials } from './Socials';

const aboutStyle = {
    width: "100vw",
    height: "100vh",
    backgroundColor: "black",
    color: "white"
};

const name = {
    paddingTop: "12%",
    fontSize: "70px",
    letterSpacing: "5px",
    textAlign: "center"
};

const aboutText = {
    paddingTop: "5%",
    width: "75%",
    margin: "auto",
    fontSize: "20px",
    lineHeight: "1.8"
};

export default function About() {
    return (
        <Card style={aboutStyle}>
            <Card.Body>
                <Card.Title style={name}>D'Andre Campbell</Card.Title>
                <Card.Subtitle className="mb-2 title-container">Developer & Photographer</Card.Subtitle>
                <Card.Text style={aboutText}>
                    Hey, my name is D’Andre. I’m a self taught, aspiring Front End Developer based out 
                    of New York. I choose this area of development because it allows me to combine my 
                    creative side with my love for coding. Currently, I’m working in the IT field as 
                    a Technician but I’ve always had an interest in the Development field. I have 4 
                    years of programming in various different languages but currently React.js is my 
                    main focus.
                </Card.Text>
            </Card.Body>
        </Card>
    )
}


