/* eslint-disable jsx-a11y/anchor-has-content */
import React from 'react';
import { Button, Row, Col } from 'react-bootstrap';
import styled from 'styled-components';
import './Projects.css'
import { ProjectItems } from './ProjectList';
import photosite from './img/visualsbydandre.jpg';
import comparts from './img/choosemyparts.png';
import cryptosite from './img/cryptoupdates.png';
import Footer from '../footer/Footer';

const screenshots = [
    photosite, comparts, cryptosite
];

const ProjectList = styled.div`
    padding-top: 7%;
    width: 100%;
    height: 100%;
`;

const Title = styled.h3`
    display: flex;
    justify-content: center;
    text-decoration: underline;
    padding-bottom: 5%;
    font-size: 44px;
`;

const projectTitle = {
    fontSize: "24px",
    color: "rgb(39, 221, 221)",
    display:"flex", 
    justifyContent:"center", 
    alignItems:"center"
};

const btnStyle = {
    backgroundColor: "rgb(39, 221, 221, .9)",
    color: "black",
    fontSize: "20px",
    border: "none",
};

const End = styled.span`
    display: flex;
    justify-content: center;
    margin: auto;
    width: 90%;
    font-size: 18px;
    font-weight: bold;
    padding-top: 50px;
`;

const centering = {
    display:"flex", 
    justifyContent:"center", 
    alignItems:"left"
};  

export default function Projects() {
    return (
        <ProjectList id="projects">
            <Title>Projects</Title>
                {ProjectItems.map((item, index) => (
                    <Row>
                        <Col md={7} style={{display:"flex",justifyContent:"center",alignItems:"center", paddingBottom:"50px"}}>
                            <img src={screenshots[index]} alt={item.alt} width="600px" height="auto" />
                        </Col>
                        <Col md={5} style={{paddingTop:"40px"}}>
                            <h2 style={projectTitle}>
                                {item.website}
                            </h2>
                            <ul style={centering}>
                                {ProjectItems.map((item, index) => (
                                    <li><i className={item.icons[index]} /></li>
                                ))}
                            </ul>     
                            <div style={centering}>
                                <Button style={btnStyle} href={item.url}>
                                    Visit Website
                                </Button>                                   
                            </div>   

                        </Col>
                    </Row>  
                ))}
            <End>
                My other coding projects can be found on my Github. 
            </End>

            <Footer />
        </ProjectList>
    )
}
